(window.webpackJsonp=window.webpackJsonp||[]).push([[461],{803:function(t,p,s){"use strict";s.r(p);var v=s(0),_=Object(v.a)({},(function(){var t=this,p=t._self._c;return p("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[p("p",[t._v("本文写于： "),p("code",[t._v("2014年4月24日 周四")]),t._v("。")]),t._v(" "),p("p",[t._v("周五学校将举办春季运动会，有明显迹象于周一，各系，班都开始了一个叫做列队走场的训练，而正是在这个过程里，我看了想了许多，终于此刻动笔记录下感慨。")]),t._v(" "),p("p",[t._v("这几天无论是我看的电影，还是贴吧，还是身处的环境，督促着我思考“人间”这两个字，这并不笼统，而是可从一言谈，一举止，一花草，一呼吸的点滴生活里考量的。现在来说，就我所思考的，则真使我心痛，伤神，愤恨，在当下午又一次彩排之后，我彻底不淡定了，决定作文审判。")]),t._v(" "),p("p",[t._v("问题往往是自上而下的，那么我也就自上而下的一一讨论。")]),t._v(" "),p("p",[p("img",{attrs:{src:"https://t.eryajf.net/imgs/2023/10/1697379033141.jpg",alt:""}})]),t._v(" "),p("p",[t._v("一个运动会的意义应在于运动员，比赛成绩的吧，然而在这里我却见识到了不一样的“景色”，作为一个男生，我从未从院系或是班级那里接听到关于运动员，运动项目之类的言论（或许这只是本系未做到）。我所看到的只是一次又一次的要求去操场练习队列，以及由此而引发的一次又一次不愉快。我记得窦文涛有一句话说“在中国往往一件事情会变成另一件事情”。在当今天下午又见军乐队，国旗护卫队，彩旗队等的一遍又一遍的彩排时，我又一次认同了文涛的话。我不知道这么大张旗鼓，大费周章，大轰大嗡的做如此多劳民伤财又与比赛无关的工作是为了什么？想来想去只能用一个词来解释----形式主义。我想，这些工作几乎不需要领导的话就自然而然地搞起来了，甚至连领导也觉得这些事情是自然而然，乃至理所应当地发生的，我不知道为什么没有一个人可怜，或者心疼在这个过程里的人儿，以及资源。人人恨形式主义，反对形式主义，但却都受着其苦，在这些人里，我首先想批评的就是能够改变这些但却什么都不做的领导们，正是他们自以为聪明的愚蠢和全局苦心，实力炫耀（这词儿着实已太仁慈）的弄巧成拙，造就了这种局面，一种已使那些工作的人都觉得开运动会不搞搞全民的队列，衣着，就是错了的局面，呵，写的我自己都想笑。")]),t._v(" "),p("p",[t._v("糊涂的人会这么辩言，搞出一年一度的运动会，弄点儿文艺节目助兴，这没什么错吧？旁的糊涂的人一副词一，但聪明的请告诉我为什么运动会重要的不是比赛，而是这些为什么那么多不参赛的人要因为走那么一个过场而集体买衣服而受累受气，又为什么那么多社团里的人才排一遍又一遍儿，在主席台上的话筒却依旧那么颐指气使？盛气凌人，在请问那名木浆坐在台上的人是谁？那些人究竟与彩排的人干什么关系？")]),t._v(" "),p("p",[p("img",{attrs:{src:"https://t.eryajf.net/imgs/2023/10/1697379179295.jpg",alt:""}})]),t._v(" "),p("p",[t._v("“我站在茫茫的人间，看世界在变，自己也在变，，，”每当想起这首歌我就会沉默于深思之中，不知道会不会在哪一天我会被同化到什么也看不见，被改变到自己也讨厌的样子。但就看我的表现而言，我为自己担忧。")]),t._v(" "),p("p",[t._v("开篇我就有提到自上而下，但我想一个命令的自上而下绝不仅仅是它本身的自上而下，在传达的过程中它早已被附加了很多东西（又一中国特色）诸如利益，淫威，，，可以想象一个从大领导那里出来的命令，在经过院系，班等环节到达学生身上，其相差会有多少倍！可我还是不明白，离学生最近的是辅导员，可为什么我丝毫见不到辅导员是爱护自己的学生呢？我反而看到其因这些而发火与班长结梁子。至于我，则只是沉默。这或许没什么错，但我总是能听到自己的内心谴责。我更疑惑的问题是：是什么造就了辅导员的一党独大，是什么使集体成了一言堂？这问题不闹清楚，不解决，则大学教育，永难有大收获。")]),t._v(" "),p("p",[p("img",{attrs:{src:"https://t.eryajf.net/imgs/2023/10/1697379189488.jpg",alt:""}})]),t._v(" "),p("p",[t._v("在上午我发了一个说说：我在想，领导的意义是什么？因为很多人并不知道领导是干嘛的，他们以为领导就是指挥指导的。甚至连被领导的人也这么认为，但我想这根本不是领导，这是压榨剥削的主子。领导的意义在于提供平台出来，相信手下的能力，然后使之在可保护的范围内自由发挥，而不是对一切都插手，干预。")]),t._v(" "),p("p",[t._v("哎，人间！茫茫大大，忙忙碌碌，已不仅是众生喧哗啦！")]),t._v(" "),p("p",[t._v("李启龙于晚上")]),t._v(" "),p("p",[p("img",{attrs:{src:"https://t.eryajf.net/imgs/2023/10/1697379198302.jpg",alt:""}})])])}),[],!1,null,null,null);p.default=_.exports}}]);