(window.webpackJsonp=window.webpackJsonp||[]).push([[462],{805:function(t,s,p){"use strict";p.r(s);var e=p(0),n=Object(e.a)({},(function(){var t=this,s=t._self._c;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("p",[t._v("本文写于： "),s("code",[t._v("2014年4月25日")]),t._v("。")]),t._v(" "),s("p",[t._v("由于女朋友起晚，在当我二人吃完饭赶过去时，就已有很多人集合了，那边声乐震天，可以想见有不少人早早起来一忙了很久了，但此时已零星有雨，在这种情况下，仍站等了一个多小时才开始缓缓入场，而在我班走时，雨已有些泛滥，于是只听那边儿管事儿的一劲儿催促，“快走，快走”。天凉寒，女生们穿的还是裙子，而准备了很久的用心，用情，用力的东西在偌大场面下，显得小的让我也吃了一惊，不知道是否会有辅导员意识到自己那无知的错----花了钱，费了心，委屈了自己的学生（不知为何，在这个地方本应是孩子，但我却不敢写，失望若此矣），最后换来的却是，，，是什么我也不知道！走完过场，雨已下的可以，我左右又看到了一些老师方队，乃至保安方队，而他们反而东张西望新鲜一切，我的心隐隐生疼。接着由主持者宣布升了国旗，校旗，然后由一个（好像是院长，我不确定）人致开幕词并宣布开幕，而这一个人竟然全按稿子的读了下去，从头到尾全无一句与雨有关，与下边那多被淋着的人有关，或是与他那可恶的稿子无关的话，这就是我昨天谈过的，在中国往往事大于人，乃至大于天，当事情已经进入状态，则连学校最大领导也变得听之任之，唯之诺之。任凭老天下雨，也阻拦不住其行进，这可算一奇观。在“独立之人格，自由之思想”，极度缺乏的大学校园，很多很多不正常，不应该，不可能变得正常，应该，可能，这是悲哀之所在。")]),t._v(" "),s("p",[s("img",{attrs:{src:"https://t.eryajf.net/imgs/2023/10/1697379208751.jpg",alt:""}})]),t._v(" "),s("p",[t._v("领导毫无幽默地念完了稿子，之后全员解散，雨水登场。")]),t._v(" "),s("p",[t._v("为什么把所有人束着的那个框框总是冲不破呢？哎！")]),t._v(" "),s("p",[t._v("于下午。")]),t._v(" "),s("p",[s("img",{attrs:{src:"https://t.eryajf.net/imgs/2023/10/1697379217783.jpg",alt:""}})])])}),[],!1,null,null,null);s.default=n.exports}}]);