(window.webpackJsonp=window.webpackJsonp||[]).push([[509],{851:function(_,v,p){"use strict";p.r(v);var t=p(0),e=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("上个世纪九十年代，退伍返乡后，我一个农村户口兵，是没有工作可言的。在部队学的开车手艺，由于缺少人脉关系再加上当时车比较少，没有找到合适的活，便被搁置了起来。")]),_._v(" "),v("p",[_._v("于是便从父辈们手中接过来赶牛的皮鞭，干起了读书时老师经常提起的，不好好学习将来掴牛腿的行当，也就是人们常说的当农民修地球。")]),_._v(" "),v("p",[_._v("在家乡那片贫瘠的黄土地上，日出而作、日落而息，想用古老的耕种方式来延续生命的辉煌。")]),_._v(" "),v("p",[_._v("然而命运似乎总是喜欢捉弄人，幸运从来也不怎么眷顾我，设计好的人生蓝图一个也没有出现，而让我经历过的和正在经历的都是我不想要的生活。")]),_._v(" "),v("p",[_._v("尽管省吃俭用，节衣缩食，一年到头却连个过年钱也省不出来，一进入腊月，就如临大敌，开始心慌，发愁怎么筹措过年的钱，无计可施只好从牙缝里挤。")]),_._v(" "),v("p",[_._v("扳指头算算到明年新麦下来还有多长时间，从为数不多的粮食中留足口粮，该卖的都卖。小麦、玉米、黄豆，甚至包括油料作物的芝麻、菜籽等，能挤一点是一点，就连给牲口换花籽饼的红薯干都想到了，挖空心思七拼八揍也不宽绰，过年给儿子买身新衣服，妻子也要讨价还价，思虑再三，恨不得一分钱掰成两半花。")]),_._v(" "),v("p",[_._v("记得有一年收成欠佳，实在无物可卖，眼看临近春节，万不得已厚着脸皮到大舅家借四百块钱过个年。")]),_._v(" "),v("p",[_._v("妻子本没有攀比的习惯，时间长了难免也会发出一些感叹：“唉！啥时候才能跟别人家一样，想上街拿着钱都走，那就知足了”。")]),_._v(" "),v("p",[_._v("没钱就没有自信，缺失了自信就总感觉站不到人前。生活上的拮据加上精神上的压力，让我真真切切地体会到了人生的不易与生活的艰难。")]),_._v(" "),v("p",[_._v("想想那些年过的日子真是比唐僧取经都难，可以说一路都是咬着牙走过来的。")]),_._v(" "),v("p",[_._v("当你喊天天不应，叫地地不灵的时候，当你兜里比脸还干净的时候，你就会为了生存而无所顾忌，迈出勇敢的步伐，做出你今生也许不可能有的经历，一如我生命中那段卖凉粉的插曲，它本来是可有可无的。")]),_._v(" "),v("p",[_._v("今天就和大家一起回顾我那段卖凉粉的故事，以此怀念那段艰苦岁月里不一样的人生。")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://t.eryajf.net/imgs/2023/03/2932fd71d8740b8f.jpg",alt:""}})]),_._v(" "),v("p",[_._v("九十年代我们村不算太大，两个生产队有一二百户人家，分为东队和西队，老一辈喜欢以东墁和西墁来区分。")]),_._v(" "),v("p",[_._v("每当春末夏初，天气逐渐转暖的时候，勤劳朴实的村民们知道又到了一年当中卖凉粉的好季节。赚钱的机会来了，大家都争先恐后地开始张罗着搅凉粉，于是古老的村庄便开始弥漫着凉粉的味道。（这里指豌豆淀粉制作的凉粉）")]),_._v(" "),v("p",[_._v("山叔是每年最早开始卖凉粉的，俗话说“没啥不引啥”。陆陆续续会有人紧跟其后。仲夏时节，卖凉粉的队伍会达到峰值，一天能有三四十家出去卖凉粉，少说也有二十几把车子出去，（卖凉粉的交通工具，大杠二八自行车）当时农村的条件，自行车就算是比较先进的交通工具了。")]),_._v(" "),v("p",[_._v("说来也怪，方圆十几里内除了我们村，其他村基本上没有卖凉粉的，以至于人们一听到凉粉的叫卖声就知道是闫店岗的来了。")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://t.eryajf.net/imgs/2023/03/c43b748749eb7100.jpg",alt:""}})]),_._v(" "),v("p",[_._v("邻居山叔年长我一岁，自小聪慧过人，猴精猴能的，用方言说就是“猴跳里狠”，做小本生意的行家。是我们村最早靠卖凉粉赚钱者之一。每当看到他收工回来坐在大门口数钱的样子很是眼气，但见他右手拿着一匝数完的钞票往左手里一拍，趾高气扬地说：“娃子，看见没有，不到一上午的功夫，净利润几十块钱到手了，跟捡钱里一样”。")]),_._v(" "),v("p",[_._v("“还等啥哩，是不是磨不开你那张穷书生的脸，脸面值多少钱？能当钱花吗？别死要面子活受罪了”。")]),_._v(" "),v("p",[_._v("“开始吧，一回生，二回熟，三回就能当师傅”。")]),_._v(" "),v("p",[_._v("说完山叔便哈哈大笑起来。")]),_._v(" "),v("p",[_._v("那笑声不仅完美地诠释了他的满足与自豪，也让我那颗羡慕的小鹿不禁乱撞了起来。")]),_._v(" "),v("p",[_._v("那个时候的农村老家，一天能挣几十块钱，人们也许没有啥概念了，打个比方你就明白了。二零零三年我建楼房时，建筑队工人一天到晚脚手不识闲儿，劳动强度又大，到最后包括一盒群英会烟在内，一个工才划十七块五。")]),_._v(" "),v("p",[_._v("山叔数钱的动作一直在我脑海里打转，一分钱难倒英雄汉，没钱花的现实就摆在面前，人总要学会改变自己，经过激烈的思想斗争，最终对金钱的渴望打败了裹在我脸上那层虚伪的面纱，决定撕破脸皮放手一搏。")]),_._v(" "),v("p",[_._v("卖凉粉的家当并不复杂，就是在自行车后座上用两根结实的棍子担起两个竹篓，用铁丝捆扎牢固即可。妻子看着我忙忙碌碌做这些东西的时候，知道我也想开工卖凉粉了。")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://t.eryajf.net/imgs/2023/03/8820eb25c9ed3220.jpg",alt:""}})]),_._v(" "),v("p",[_._v("知夫者，莫如妻也。她深知我性格内向，不善言谈，虚荣爱面子，卖凉粉这种靠吆喝赚钱的买卖岂能是我所驾驭得了的。")]),_._v(" "),v("p",[_._v("她将信将疑地注视着我的一举一动，我从她疑惑的眼神里看出了她想要对我说的话，妻子最终还是没忍住轻声问道：“你想好了吗？”")]),_._v(" "),v("p",[_._v("见我没理会她，又问：“你行吗？”")]),_._v(" "),v("p",[_._v("“这怎么能用疑问句呢？把“吗”字去掉，你行就对了。”")]),_._v(" "),v("p",[_._v("“鞋合不合适，只有脚知道，你不试怎么知道行不行。”")]),_._v(" "),v("p",[_._v("说这些话其实我自己心里也没底，只不过是煮熟的鸭子嘴硬罢了。")]),_._v(" "),v("p",[_._v("贤惠能干、心灵手巧的妻子见我意志坚决，也不动声色地默默配合着我。用高梁杆上截下来的葶子做了几个小锅簰，大小以能盖住盆沿为准。因为叠加的凉粉盆要有硬实的簰子做支撑，要不然上面的一盆凉粉把下面的一盆压烂就没法卖了，毕竟货卖一张皮吗。")]),_._v(" "),v("p",[_._v("凉粉的制作工艺并不复杂，根本不用拜师学艺，美味佳肴往往只需要最简单的烹饪方式，明眼人一看就能学会。")]),_._v(" "),v("p",[_._v("搅凉粉关键在于掌握好淀粉的量，多了少了都不合适。")]),_._v(" "),v("p",[_._v("偷工减料搅出来的凉粉它就软，从盆里不好往外拿，人们常说买土豆还得挑挑，买西瓜不由地都想敲敲，卖凉粉也是如此，挑剔的买主一样不好糊弄，买之前会先用手摁一下，太软他就不买你的帐扭头就走。淀粉过量做出来的凉粉太硬也不理想，硬就脆，吃起来少了那种爽滑绵软的感觉。")]),_._v(" "),v("p",[_._v("一盆十五斤左右的凉粉需用两斤豌豆淀粉，这里指买回来的湿粉圪垯，含有水分，干淀粉就用不了这么多。时间久了有经验的师傅根本不用称，根据凉粉在锅里搅动的粘稠度就知道做出来凉粉的软硬，用卖油翁那句话说“无他，惟手熟尔”。")]),_._v(" "),v("p",[_._v("搅凉粉和炖肉不同，炖肉是大火烧开文火慢炖，搅凉粉则一直都是大火，不需要小火，要一气呵成。")]),_._v(" "),v("p",[_._v("待水烧开之时，需往锅里放入一小勺食盐，盐是五味之首，加入盐食材就有了精气神，加食盐的好处在于做出来的凉粉既结实，颜色又好看。")]),_._v(" "),v("p",[_._v("也有加白矾的，但一般不建议使用，虽然做出来的凉粉会更白更好看，但白矾稍有毒性，长期或过量食用有害人的身心健康。")]),_._v(" "),v("p",[_._v("事先和好的粉面糊下锅之前，一定要把它再次搅拌均匀，因为淀粉极易沉淀，放置三五分钟它就会分层，上面是水，下面是淀粉，而且淀粉粘附力极强，粘在盆里倒不出来。")]),_._v(" "),v("p",[_._v("把搅拌好的淀粉糊缓缓地倒入锅中，此时要用勺子不停地快速搅动，以便淀粉糊和沸水充分交融，不久锅里便会不停地冒出大泡，不要以为此时凉粉已经熟了，还要继续大火烧五六分钟，不熟的凉粉口感会差很多，不仅不筋道而且吃着口感是面的。没出锅前的凉粉需不停地搅动以免糊锅，勺子要口朝上搅，口朝下搅容易把沾在锅上的一层锅圪渣刮下来混入锅中影响凉粉的品质，确认完全熟透便可起锅装盆。")]),_._v(" "),v("p",[_._v("搅好的凉粉白莹莹、颤悠悠，细腻如油脂，精莹剔透，看似柔嫩，却富有弹性，放在院里小方桌上，过夜之后第二天早上便会凝固。")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://t.eryajf.net/imgs/2023/03/a21c5b24d2fd03fa.jpg",alt:""}})]),_._v(" "),v("p",[_._v("有人说，好女人就是愿意守着丈夫一起过苦日子的女人。每当我搅凉粉的时候，妻子总是忙前忙后地做着准备工作，烧锅、刷锅以及一些打下手的琐碎活都由妻子承担，就跟铁匠打铁一样，不是帮你拉风箱，就是帮你抡大锤，人们常说“跟着当官的做娘子，跟着杀猪的翻肠子”，多年以后我才明白后半句的真正内涵，只有真心爱你、死心塌地跟你过日子的人才会心甘情愿地为你付出，再苦再累也不嫌你穷。")]),_._v(" "),v("p",[_._v("西墁炳山哥和焕荣嫂子是庄上唯一夫妻俩卖凉粉的。两个人出去每天至少要搅八盆凉粉，旺季时两人一天能卖十来盆，为省去刷锅洗锅的麻烦，干脆在院里盘一口大锅，八盆凉粉一次完成。")]),_._v(" "),v("p",[_._v("焕荣嫂子天生丽质，美丽大方，不仅能说会道，而且富于开拓精神，巾帼不让须眉，是村上仅有的两位女性卖凉粉者之一，也是后来鸟枪换炮唯一骑摩托卖凉粉的女强人，记得好像只有她出去卖凉粉带过调料，把凉粉送到田间地头，收麦季节人们在地里、场里干活，累了饿了也能喝个现成。真要评的话，毫无争议算得上是庄上卖凉粉的形象代言人，人送外号“凉粉西施”。")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://t.eryajf.net/imgs/2023/03/4b689e82c9bc4521.jpg",alt:""}})]),_._v(" "),v("p",[_._v("头天晚上搅好四盆凉粉，第二天上午九点我便出发了。")]),_._v(" "),v("p",[_._v("骑着家里唯一可以拿得出手的破旧二八自行车，载着后座上竹篓里的四盆凉粉，不咋好使的脚蹬子每转一圈便吱吜一声，后轮钢圈呲着大梁，也不甘落后地转一圈便呲啦一声。")]),_._v(" "),v("p",[_._v("吱扭、呲啦，吱扭、呲啦似乎有点不情愿地行驶在过去还没有村村通的乡村土路上。")]),_._v(" "),v("p",[_._v("那刺耳的吱扭声让我感觉就象左手摁个穷蝎子，那逆耳的呲啦声又象是右手摁个穷蚂蜂，不管是蝎子蜇来还是蚂蜂拧，说到底都是贫穷带来的不受用。")]),_._v(" "),v("p",[_._v("肢体上的劳累倒在其次，精神上的压力才最折磨人。不和谐的吱扭呲啦声使我本来就不瓷实的自信心大打折扣。")]),_._v(" "),v("p",[_._v("老话说：“干啥说啥，卖啥吆喝啥，干东行不说西行，卖萝卜不说猪羊”。卖凉粉你不吆喝谁知道你葫芦里卖里啥药，别看我平常在家里吆五喝六的，那也只是老鼠杠枪窝里横，真使到正杆上没一点，“卖凉粉”这几个字就象赖蛤蟆喝胶水一样，实在是让我张不开嘴。")]),_._v(" "),v("p",[_._v("开弓没有回头箭，既然信誓旦旦地推着凉粉车子出来了，绝不能认怂。假若就此偃旗息鼓，不战而退那岂不是让人笑掉大牙，对自己的自尊心更是一次无情的打击，无异于自搧耳光。")]),_._v(" "),v("p",[_._v("“凉-粉”在进庄之前没人的路上我试着亮一嗓子。")]),_._v(" "),v("p",[_._v("不中不中，咋听咋不象，连我自己都没相中，既不洪亮又不协调，尽管只是一种兜揽生意的叫卖声，但它也是一门学问，讲究一定的方式和技巧，虽不象唱戏一样做到字正腔圆，但也不能野腔野调地招人烦，最起码听起来让人感觉舒服。")]),_._v(" "),v("p",[_._v("我开始琢磨着咋喊才能合辙压韵，听着顺耳。")]),_._v(" "),v("p",[_._v("我突然想起来每天早上还没进村就开始吆喝的邻庄老刘卖豆腐的声音，他把“豆“字的音符拉的很长，而“腐”字很短，抑扬顿挫把握的恰到好处，听起来简洁明亮又有穿透力，从东墁进庄西墁都能听见，颇具烟火气息。")]),_._v(" "),v("p",[_._v("我比葫芦画瓢又试一嗓子，不拈弦，还不如第一嗓子喊的呢，总觉得还差点啥，我在寻思的过程中发现，要单喊凉粉两字，比较干脆生硬不符合我腼腆的性格，思虑再三我决定在凉粉的后边加个感叹词“呢”。“凉-粉-呢”，这下似乎找到感觉了。")]),_._v(" "),v("p",[_._v("一路思来一路想，在惶恐不安之中来到了下宋村，没出发前就听说这庄下货（卖里快），爱喝凉粉。")]),_._v(" "),v("p",[_._v("做小本生意的一般进村就开始吆喝，除了自行车的吱吜呲啦声，而我却像鬼子一样悄悄地进了村。")]),_._v(" "),v("p",[_._v("在村中间小卖部前停住了脚，心想做生意的地方肯定人不会少。")]),_._v(" "),v("p",[_._v("“哟，卖凉粉里来恁早，地里干活的人还没回来呢，这一会儿打凉粉的人不会多，要等地里的人回来才中。”")]),_._v(" "),v("p",[_._v("“这有椅子，你先坐这儿歇会儿，别着急。”")]),_._v(" "),v("p",[_._v("好心的小卖部女主人抢先给我搭讪。")]),_._v(" "),v("p",[_._v("我点点头说：“是有点早，不着急，等会吧。”")]),_._v(" "),v("p",[_._v("第一次卖凉粉没经验，心想卖凉粉的人多，要早点走抢占先机，其实则不然，悠乡卖凉粉的黄金时间段是午饭前的十一至十二点，非要等地里干活的人回来才会有人买，春耕大忙季节，谁也不会为了单喝凉粉而在家里等着，而一旦吃过午饭，你就是喊破喉咙基本上也没人买了。")]),_._v(" "),v("p",[_._v("那些年的种植结构种烟、朝天椒和麦套棉是主打品牌，也是农民们一年到头眼扒眼望的主要经济来源。")]),_._v(" "),v("p",[_._v("收麦前是烟苗、小辣椒苗和棉花苗移栽的攻艰阶段，在烟地里、麦田里钻来拱去干一晌活下来，不仅热的难受，而且累的够呛，在瓜果没下来之前，凉粉无疑是解暑的理想食品，听到凉粉的叫卖声一般都会心动，尽管有些过日子仔细的人家有点舍不得。")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://t.eryajf.net/imgs/2023/03/48888991e38961e3.jpg",alt:""}})]),_._v(" "),v("p",[_._v("十一点左右，地里干活的人陆陆续续开始回来了。")]),_._v(" "),v("p",[_._v("“凉-粉-呢，卖凉-粉-呢”，我抓住时机喊了两声。")]),_._v(" "),v("p",[_._v("话音刚落，老远就看见一个小媳妇擓着筐子拿着盆子出来了。")]),_._v(" "),v("p",[_._v("我觉得我天生就不是做生意的料，没人来时盼人来，真来人了心里又扑通扑通跳的厉害，感觉就跟相亲一样，没见到对方时翘首以盼，真见到人了又觉得有点尴尬，手心里只冒冷汗，更何况卖凉粉这个活，真是大姑娘上轿一一头一回，总觉得害赖，丢人。")]),_._v(" "),v("p",[_._v("“喂，卖凉粉哩，哪庄的？是闫店岗里不？咋没见过你哩？你刚才喊的可有点生，不够自信，再喊几声让俺给你指点指点”。")]),_._v(" "),v("p",[_._v("小媳妇说完，抿嘴笑了起来。")]),_._v(" "),v("p",[_._v("我知道她在拿我寻开心，也不与理会，便取出秤开始称粮食。")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://t.eryajf.net/imgs/2023/03/63813335880ac6a2.jpg",alt:""}})]),_._v(" "),v("p",[_._v("当时拿钱打凉粉的比较少，十之八九都是用粮食换，小麦、玉米、红薯干啥都中，各作各价。")]),_._v(" "),v("p",[_._v("称罢粮食，我从竹篓里取出一盆凉粉，用刀十字划开，刀要蘸水，要不然会把凉粉划的豁豁牙牙的不好看。")]),_._v(" "),v("p",[_._v("有经验的能做到一刀准，斤两不差，而我哆哆嗦嗦的唯恐给人家少了，不是多四两，就是多半斤，多了也不去（掉），买东西的心理是添点心里舒服，去点心里就不得劲。")]),_._v(" "),v("p",[_._v("“看你那笨手笨脚不熟练劲儿，是不是以前没卖过。多半斤哩，你不去（掉）了”。")]),_._v(" "),v("p",[_._v("小媳妇依然笑着说。")]),_._v(" "),v("p",[_._v("“对，今个儿是我头一回出来卖凉粉，去啥里，搁不当里，端回家吃吧”。")]),_._v(" "),v("p",[_._v("我小声地应着。")]),_._v(" "),v("p",[_._v("“你可真大方，明天来还打你的凉粉”。")]),_._v(" "),v("p",[_._v("小媳妇满心欢喜地端着凉粉走了。")]),_._v(" "),v("p",[_._v("半个小时左右，原地没动卖了两盆，腾出手来我又鼓起勇气喊了两声。")]),_._v(" "),v("p",[_._v("“别喊了，挪挪地方吧，这一片儿该买的都差不多了，往东北墁去，那儿有几家喜欢喝凉粉的”。")]),_._v(" "),v("p",[_._v("小卖部女主人又主动给我指点迷津。")]),_._v(" "),v("p",[_._v("言谢之后便开始收拾换来的粮食，准备挪地方。")]),_._v(" "),v("p",[_._v("忽然耳边传来一声凉粉的叫卖声，听声音判断是山叔来了，卖凉粉的碰头是常有的事，因为哪庄生意好做，卖的快，大家都心知肚明。")]),_._v(" "),v("p",[_._v("“娃子，来里怪早里，终于下水了，咋样，卖多少了”？")]),_._v(" "),v("p",[_._v("“卖两盆了”。")]),_._v(" "),v("p",[_._v("“我早就叫你开始，你拉不下你那张脸，万事开头难，以后慢慢就顺了。你第一回出来，你搁这儿卖吧，凭我的经验应该不出庄就能卖完，我换个庄”。")]),_._v(" "),v("p",[_._v("山叔说完，笑呵呵地蹬上自行车走了。目送山叔远去的背影，心里莫名升起了一股感激之情。")]),_._v(" "),v("p",[_._v("来到东北墁之后，果真如此")]),_._v(" "),v("p",[_._v("一会儿功夫就卖完了剩下的两盆凉粉。")]),_._v(" "),v("p",[_._v("旗开得胜，初战告捷，极大地鼓舞了我的士气，喜悦的心情难以言表。尽管卖凉粉换来的粮食比四盆凉粉重得多，但我一点也不觉得累，尽管自行车的吱吜、呲啦声会更响一点，我也不再觉得它有多么刺耳，权且把它当作改变我贫穷命运而奏响的命运交响曲吧！")]),_._v(" "),v("p",[_._v("“……今天是个好日子，心想的事儿都能成，明天又是好日子……”，回家的路上激动的心情让我不由地哼起了当时流行的《好日子》。感觉生活充满了希望，幸福似乎就握在自己手上。")]),_._v(" "),v("p",[_._v("卖凉粉带来的经济效益暂时改变了生活的窘况。手里有了零花钱也开始舍得投资了，换了吱扭吱扭响的脚蹬子，也校正了呲啦呲啦响的后轮钢圈。打破惯例给妻子也置买了一身新衣服，那年头手头紧哪敢放肆，农村人一年到头也难得添身新衣裳。")]),_._v(" "),v("p",[_._v("隔三差五也敢割块肉打打牙祭了，平常没钱舍不得买的六毛钱一盒的“顺和”烟，也敢买着抽了，出门办事才买的“群英会”烟，偶尔也会买上一盒讲讲排场。")]),_._v(" "),v("p",[_._v("人生就像一场漫长的旅行，你面临的不可能全是金光大道，偶尔也会走走弯路，打个趔趄什么的。卖凉粉亦是如此，它就像象卖西瓜、摊麦打场一样，时刻要关注天气的变化，天气越好卖的越快。那个年代没有手机，人们只有从收音机或是电视机上获得天气预报的信息。")]),_._v(" "),v("p",[_._v("有时候预报可真是预报，没个准头，报的明天是多云转阴，部分地区有时有零星小雨，遇到这种模棱两可的情况，搅与不搅常常让卖凉粉的人家举棋不定，处于两难之中，此时大家往往会互相观望，一旦有人搅了，金钱的魅力会促使其他人家跟着行动起来，就象非州大草原上角马大迁徙一样，为了寻找新的草原，明明知道水里有鳄鱼虎视眈眈，一旦有同伴跳下去，其他角马就会象下饺子一样，奋不顾身地往下跳，生死的事就只好交给命运了。")]),_._v(" "),v("p",[_._v("来到山叔家，院子里放着他早已搅好的几盆凉粉。")]),_._v(" "),v("p",[_._v("“搅没有，娃子”。")]),_._v(" "),v("p",[_._v("“天不咋着，一直犹豫着呢”。")]),_._v(" "),v("p",[_._v("“搅吧，撑死胆大的，饿死胆小的，说是有小雨，只要下不来，不愁卖不出去，外庄人都等着喝咱庄凉粉呢”。")]),_._v(" "),v("p",[_._v("回到家妻子凑上来问：“啥情况，有人家搅没有，咱搅还是不搅”？")]),_._v(" "),v("p",[_._v("“我看还是算了吧，没把握的事咱最好不抱希望，东山日头一大堆呢”。")]),_._v(" "),v("p",[_._v("妻子又自言自语地补充着。")]),_._v(" "),v("p",[_._v("“娃，我听天气预报说，南阳不分地区都有雨，咱还是别趟那浑水了”。父亲在一旁不失时机地插话说。（很显然父亲是把“部分地区”听成“不分地区”了）")]),_._v(" "),v("p",[_._v("“做饭吃，不搅了”。")]),_._v(" "),v("p",[_._v("在犹豫不决中我做出了最后的决定。")]),_._v(" "),v("p",[_._v("老天爷的心思有时候真是让人琢磨不透，没有想到第二天并没有下雨，天气还凑合，让人真正明白了什么叫阴晴不定。看着同村卖凉粉人一个个凯旋而归，心里难免有点羡慕，毕竟因为决策失误少挣几十块钱，但并没有嫉妒的心理，同是天涯沦落人，祖祖辈辈都在一个村子住着，大家都不富裕，没有必要也犯不着。")]),_._v(" "),v("p",[_._v("五六月里说风就是雨，天气就像小孩子的脸，说变就变。本来头天下午碧海蓝天、风平浪静的，卖凉粉的人家都早早地搅好了凉粉，任谁都不会相信老天爷会给他们开玩笑。")]),_._v(" "),v("p",[_._v("让人始料不及的是第二天天气突变、电闪雷鸣、风雨交加，突如其来的变化让头天晚上搅凉粉的人家弄的措手不及，一场雨下来给人们浇了个透心凉。")]),_._v(" "),v("p",[_._v("拿摊麦打场来说这种情况就等于是塌场了，无法收场。")]),_._v(" "),v("p",[_._v("此时搅凉粉的人家就像六月里喝冰水一一寒了心，无可奈何只好这家送一块，那家送半盆，多少随意，够本就行。")]),_._v(" "),v("p",[_._v("回忆那些卖凉粉的日子，经过风，淋过雨，磕磕绊绊一路走来，有人总结出的一套顺口溜用在卖凉粉人身上再贴切不过了。")]),_._v(" "),v("p",[_._v("天气一赖，卖里不快；")]),_._v(" "),v("p",[_._v("天一刮风，卖着不中；")]),_._v(" "),v("p",[_._v("天气一阴，卖着担心；")]),_._v(" "),v("p",[_._v("天气一冷，卖着得等；")]),_._v(" "),v("p",[_._v("天气一寒，卖着做难；")]),_._v(" "),v("p",[_._v("天气一下，卖着害怕；")]),_._v(" "),v("p",[_._v("天气一晴，卖着还行；")]),_._v(" "),v("p",[_._v("天气一热，人人想喝；")]),_._v(" "),v("p",[_._v("天气越好，卖完越早。")]),_._v(" "),v("p",[_._v("说实在的卖凉粉的永远也不想和卖炭翁碰头，虽然出发点相同，都是为了身上衣裳口中食，但彼此心里盼望的天气却有天壤之别，卖碳翁是心忧炭贱愿天寒，卖凉粉人盼的却是天热凉粉早卖完。")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://t.eryajf.net/imgs/2023/03/7e80915c5b07ef50.jpg",alt:""}})]),_._v(" "),v("p",[_._v("卖凉粉在过去的农村属于季节性生意，一般能卖到麦天，割麦打场是一年当中凉粉生意最后的辉煌。")]),_._v(" "),v("p",[_._v("焦麦炸豆的季节，天又热来人又累，何以解暑，唯有凉粉，到哪庄几乎不隔家都要打点凉粉。")]),_._v(" "),v("p",[_._v("繁忙的麦季需要劳力干活，腾不出人手的人家就此收手，卖凉粉的队伍逐渐减小，买家却是日益增多，为数不多的几家卖凉粉人加班加点，边收麦边卖凉粉，早上摊好场出去，基本上十一点左右就能回来，还能赶上碾好的场翻一遍，忙上加忙累自不必说，金钱的魔力往往能给人们带来无穷的动力，象山叔，焕荣嫂子这些元老级别的卖凉粉人会一直坚持到最后。")]),_._v(" "),v("p",[_._v("麦罢，瓜果一旦下来，就很少有人再喝凉粉了，特别是西瓜上市以后，人们就不再乞求于凉粉的魅力了。")]),_._v(" "),v("p",[_._v("也不知从何时起，村子里轰轰烈烈的卖凉粉大军慢慢地消声匿迹了。当年那走村串巷见证历史变迁的卖凉粉的吆喝声，也渐渐地消失在了街头巷尾，逐渐移出了人们的生活。")]),_._v(" "),v("p",[_._v("人活一世不容易，一辈子很短，眨眼就过完，你不一定非得富有，但也总要顺应时代的朝流，活得像那么回事，不枉人间走一回。")]),_._v(" "),v("p",[_._v("眼看着左邻右舍一座座小楼如雨后春笋般拨地而起，而我家那座低矮的旧瓦房，就像羊群里跑个驴显得格外扎眼，破门露院的，连个大门也没有。")]),_._v(" "),v("p",[v("img",{attrs:{src:"http://t.eryajf.net/imgs/2023/03/0cf042c0be85c28f.jpg",alt:""}})]),_._v(" "),v("p",[_._v("每逢过年赶年集，妻子就会像祥林嫂一样反复地重复着：“看见人家买大门对联就眼气，啥时候咱也能像人家一样贴上大门对联呀”！")]),_._v(" "),v("p",[_._v("残酷的现实让我清醒地认识到单靠每年季节性的卖凉粉，也只能解决一时的燃眉之急，仅凭那一亩三分地微薄的收入，要搁现在来说，就是干到胡子白也娶不起媳妇买不起楼。")]),_._v(" "),v("p",[_._v("人情的冷暖，世道的艰难，让我总想摆脱命运的辘轳，活出个样子给自己看。")]),_._v(" "),v("p",[_._v("二千年之后，为圆妻子能贴上大门对联的梦想，虽然视土地如生命的我有点故土难离，但还是毅然决然地踏上了南下的火车，暂时告别了几千年遗留下来的农耕文明，背井离乡，从此开始了漂泊流离的打工生涯。")]),_._v(" "),v("p",[_._v("一晃二十多年过去了，岁月的风沙荡平了我心中的羞涩，流失的时光带也走了我内心的徬徨。")]),_._v(" "),v("p",[_._v("当年那个为了生计走村串巷卖凉粉的愣头小伙子，转眼也到了知天命的年龄，再没有了闲情逸致与春风对酒当歌，也不再象自然界中某些雄性动物一样，用自身的气味来标注自己的势力范围了。那一个个曾经奋斗过的梦想，最终都变成了美丽的传说。")]),_._v(" "),v("p",[_._v("人们总是喜欢在风尘中寻找自己从前的样子。回望来时走过的路，早已被匆匆流逝的岁月所掩埋，破碎的心却难以填平昨日留下的遗憾。唯有当年那羞涩、困惑、无奈的卖凉粉声却时常萦绕在耳旁，从来也不需要想起。")]),_._v(" "),v("p",[_._v("“凉-粉-呢”")]),_._v(" "),v("p",[_._v("卖“凉-粉-呢”")]),_._v(" "),v("p",[_._v("……")]),_._v(" "),v("p",[v("code",[_._v("后注：")]),_._v("此文首发在"),v("a",{attrs:{href:"https://mp.weixin.qq.com/s/fPl9Atust_8IJHQsbelidg",target:"_blank",rel:"noopener noreferrer"}},[_._v("《乡土中原》"),v("OutboundLink")],1),_._v("公众号，同步在此，时常拜读。")])])}),[],!1,null,null,null);v.default=e.exports}}]);