(window.webpackJsonp=window.webpackJsonp||[]).push([[533],{873:function(t,n,e){"use strict";e.r(n);var p=e(0),v=Object(p.a)({},(function(){var t=this,n=t._self._c;return n("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[n("p",[t._v("整理自父亲发的微信朋友圈，分享时间为："),n("code",[t._v("12月21日")])]),t._v(" "),n("blockquote",[n("p",[t._v("指山卖磨")]),t._v(" "),n("p",[t._v("在我心中一直有个伟大的中国梦，啥时候才能成为雄霸世界的有钱人，超过比尔盖茨，碾压马云。现实告诉我有点不太可能，唯一的办法就是做梦。\n别的行不行另说，我做梦的实力那是杠杠的，和赵本山有得一拼，经常在梦中笑醒的不只他一个。")]),t._v(" "),n("p",[t._v("诸永高速有一服务区叫神仙居，经过长途奔袭，带着一丝倦意车子停在了货车停车区，看着远处一座座挺拨的青山，迷迷糊糊地就进入了梦乡。")]),t._v(" "),n("p",[t._v("在梦里，青山变成了金山银山，那金灿灿的光茫带着我游沥了祖国的名山大川，所到之处都是闪闪发光的金子。因为在梦里我把山都开发成石磨给卖了，又回到了刀耕火种驴拉磨的年代，生意异常火爆，订单象雪片一样你就不知道什么时候会停。")]),t._v(" "),n("p",[t._v("下一步我准备开拓视野，大胆规划，扩大规模，勇于创新，准备把泰山开发成“泰山石敢当”，峨眉山开发成大佛，黄山开发成“迎客松”图案，嵩山开发成“少林寺十八罗汉”……，我不敢往下想了。\n这次却没有笑醒，而是服务区保安把我从美梦中拉回了现实，“这是谁的车，谁的车，怎么占了两个停车位，赶快开走”，我揉了揉朦朦胧胧的双眼，极不情愿的应了一声，“做什么青天白日梦呢，赶快开走“保安重复着说。")]),t._v(" "),n("p",[t._v("没想到，万万没想到我的黄梁美梦居然止步于一个保安的吼叫声中，指山卖磨的梦想也从此就破灭了。不过从此我落下一毛病，看见青山眼里便会冒出金灿灿的光茫。")])]),t._v(" "),n("p",[n("img",{attrs:{src:"http://t.eryajf.net/imgs/2021/09/d4622b361dd81df6.jpg",alt:"image-20210717175317433"}})])])}),[],!1,null,null,null);n.default=v.exports}}]);